                    <h2>Ready start your job seeking journey?</h2>

                    <p>1,2,3 let's go! Click the button below and we'll be in touch.</p>

                    <div class="flex justify-center space-x-1 mt-6 mb-8">
                        <button class="button button-secondary w-40" id="submit-button">Let's go!</button>
                    </div>

                    <p><span class="cursor-pointer" id="back-button">Go back</span></p>
                </div>
            </form>
            <div class="progress-bar">
                <div class="progress"></div>
            </div>

        </div>
    </div>

</div>

<script>

    const myButton = document.getElementById('myButton');
    const myModal = document.getElementById('myModal');
    const bannerLogin = document.getElementById('bannerLogin');
    const openLink = document.getElementById('openLink');
    const closeModal = document.getElementById('closeModal');

    if(myButton){
        myButton.addEventListener('click', function () {
        myModal.classList.add('show');
    });
    }
    

    if(bannerLogin){
        bannerLogin.addEventListener('click', function () {
        myModal.classList.add('show');
    });
    }
    

    if(openLink){
        openLink.addEventListener('click', function () {
        myModal.classList.add('show');
    });
    }
    

    if(closeModal){
    closeModal.addEventListener('click', function () {
            myModal.classList.remove('show');

        });
    }
    

    const form = document.querySelector('.onboarding');
    const steps = Array.from(document.querySelectorAll('.step'));
    const progress = document.querySelector('.progress');

    let currentStep = 0;

    function updateProgressBar() {
        const progressPercentage = ((currentStep + 1) / steps.length) * 100;
        progress.style.width = `${progressPercentage}%`;
    }

    function showStep(stepIndex) {
        steps[currentStep].classList.add('hidden', 'fadeup');
        steps[stepIndex].classList.remove('hidden', 'fadeup');
        currentStep = stepIndex;
        updateProgressBar();
    }

    function handleNextButtonClick() {
        const inputs = steps[currentStep].querySelectorAll('input');
        let isValid = true;
        for (let i = 0; i < inputs.length; i++) {
            if (!inputs[i].value) {
                isValid = false;
                break;
            }
        }
        if (isValid) {
            showStep(currentStep + 1);
        } else {
            alert('Please fill out all the fields.');
        }
    }

    function handleBackButtonClick() {
        showStep(currentStep - 1);
    }

    function handleSubmitButtonClick() {
        const inputs = form.querySelectorAll('input');
        let isValid = true;
        for (let i = 0; i < inputs.length; i++) {
            if (!inputs[i].value) {
                isValid = false;
                break;
            }
        }
        if (isValid) {
            alert('Form submitted!');
        } else {
            alert('Please fill out all the fields.');
        }

    }

    document.querySelectorAll('#next-button').forEach(button => {
        button.addEventListener('click', handleNextButtonClick);
    });

    document.querySelectorAll('#back-button').forEach(button => {
        button.addEventListener('click', handleBackButtonClick);
    });

    document.querySelector('#submit-button').addEventListener('click', handleSubmitButtonClick);

    const passwordInput = document.getElementById('password');
    const errorLabel = document.getElementById('password-error');

    passwordInput.addEventListener('input', function () {
        const password = passwordInput.value;
        const isValid = validatePassword(password);

        if (!isValid) {
            errorLabel.innerHTML = '<span class="text-red-500">Password must contain at least one uppercase letter, one lowercase letter, one number, and one special character.</span>';
        } else {
            errorLabel.innerHTML = '<span class="text-green-500">Strong as a rock!</span>';
        }
    });

    function validatePassword(password) {
        const regex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)(?=.*[^\da-zA-Z]).{8,}$/;
        return regex.test(password);
    }


    const confirmPasswordInput = document.getElementById('confirm-password');
    const errorConfirmLabel = document.getElementById('confirm-error');

    function comparePasswords() {
        const passwordInput = document.getElementById('password');
        const confirmPasswordInput = document.getElementById('confirm-password');

        if (passwordInput.value !== confirmPasswordInput.value) {
            errorConfirmLabel.innerHTML = '<span class="text-red-500">Not quite!</span>';
        } else {
            errorConfirmLabel.innerHTML = '<span class="text-green-500">Good to go.</span>';
        }
    }

    confirmPasswordInput.addEventListener('input', comparePasswords);

</script>